+:root   {
+    --primary-color-text: #FFca87;
+    --primary-color-link: #ffca87;
+    --primary-color-bg: #1E1E1E;
+    --primary-color-shadow: #000000; 
+
+}
+
+* {
+    margin: 0;
+    padding: 0;
+    box-sizing: border-box;
+}
+
+html, body {
+    scroll-behavior: smooth;
+    overflow-x: hidden;
+    margin: 0;
+    padding: 0;
+    box-sizing: border-box;
