+    font-optical-sizing: auto;
+    font-weight: 200;
+    font-style: normal;
+    width: 100%;
+}
+
+.page-wrapper {
+    width: 100%;
+    max-width: 1280px;
+    margin: 0 auto;
+    padding: 0px;
+}
+
+.container  { 
+    width: 100%;
+    max-width: 1280px;
+    color: var(--primary-color-text);
+}
+
+.menu   {
+    display: grid;
+    width: 100%;
+    height: 80px;
+    grid-template-columns: 2fr 1fr;
