+
+}
+
+.page-menu  {
+    position: relative; /* Add this */
+    justify-self: end;
+    margin-right: 0px;
+}
+
+.menu-list  {
+    display: none;
+}
+
+.hamburger  {
+    display: flex;
+    flex-direction: column;
+    width: 48px;
+    height: 48px;
+    justify-content: space-evenly;
+    align-items: center;
+    border: 1px solid var(--primary-color-text);
+    border-radius: 10px;
+    background-color: var(--primary-color-bg);
+    margin: 16px;
+}
+
+.line   {
+    width: 28px;
+    height: 3px;
+    background-color: var(--primary-color-text);
+    border-radius: 10px;
+    margin: 1px auto;
+}
+
+.menu-panel {
+    position: absolute;
+    right: -1000px;
+    top: 0;
+    width: 264px;
+    height: 519px;
+    background-color: var(--primary-color-link);
+    font-size: 21px;
+    text-transform: uppercase;
+    text-align: center;
+    padding: 40px 0px 20px 0px;
+    box-sizing: border-box;
+    transition: right 1s ease-in-out;
+}
+
+.menu-panel ul  {
+    list-style: none;
+    padding: 0;
+}
+
+.menu-panel ul li   {
+    margin-bottom: 32px;
+}
+
+.menu-panel ul li a {
+    color: var(--primary-color-bg);
+    text-decoration: none;
+}
+
+.menu-panel.active  {
+    right: 0;
+}
+
+.logo-span  {
+    display: inline-block;
+    margin: 15px 10px;
+    font-family: 'Unbounded', sans-serif;
+    font-optical-sizing: auto;
+    font-style: normal;
+    line-height: 0.8;
+    font-weight: 800;
+    font-size: 25pt;
+    text-transform: uppercase; 
+    color: var(--primary-color-link);
