+
+    .social-media-icons ul  {
+        list-style: none;
+        padding: 0;
+    }
+    
+    .social-media-icons ul li  {
+        display: inline-block;
+        margin: 16px;
+    }
+    
+    .social-media-icons ul li img {
+        width: 36px;
+        height: auto;
+    }
+
+    
+
+}
+
+/* HIDE ON MOBILE ONLY */
+
+
+@media (max-width: 768px) {
+        
+    .my-works-intro {
+        display: none;
+    }
+
+    .profile-text  {
+        display: none;
+    }
+
+    .contact-intro  {
+        display: none;
+    }
+
+    .contact hr {
+        display: none;
+
+    }
+
+    .get-in-touch   {
+        display: none;
+    }
+
+    .contact h3 {
+        display: none;
+    }
+
+    html, body {
+        overflow-x: hidden;
+        margin: 0;
+        padding: 0;
+        width: 100%;
+    }
+
+    .footer-content {
+        display: none;
+    }
+
+}
+
