        background-color: var(--primary-color-text);
        border-radius: 10px;
        border: 0;
    }

    input {
        height: 34px;
        margin-bottom: 16px;
    }

    .contact-form button {
        background-color: var(--primary-color-text);
        color: var(--primary-color-bg);
        font-weight: 600;
        text-transform: uppercase;
        font-size: 16pt;
        padding: 8px 16px;
        border-radius: 10px;
        border: none;
        box-shadow: 0px 5px 5px rgba(0, 0, 0, 0.3);
        margin: 0px;
        margin-left: 0;
    }

    .social-media   {
        grid-column: 2/3;
        grid-row: 2/3;
    }

    .social-media h3 {
        font-size: 40px;
    }

    .contact-details    {
        text-align: start;
        grid-column: 2/3;
        grid-row: 1/2;
        box-sizing: border-box;
        padding: 32px;
        margin-top: 30px;
    }

    .contact-details ul {
        list-style: none;
        padding: 0;
        font-size: 21px;
    }

    .contact-details ul li {
        margin-bottom: 24px;
        line-height: 1.2;
        font-size: 18px;
        font-weight: 400;
        text-transform: uppercase;
        display: flex;  /* Use flexbox */
        align-items: center;  /* Vertically align text and image */
    }

    .contact-details ul li img {
        margin-right: 20px;  /* Space between image and text */
        height: 20px;
    }

    .contact-details ul li span {
        margin-left: 20px;  /* Optional if you need the text indented as well */
    }

    .social-media-icons {
        text-align: start;
        margin-left: 10px;
    }

    .social-media-icons ul  {
        list-style: none;
        padding: 0;
    }

    .social-media-icons ul li  {
        display: inline-block;
        margin: 16px;
    }

    .social-media-icons ul li img {
        width: 36px;
        height: auto;
    }



}

/* HIDE ON MOBILE ONLY */


@media (max-width: 768px) {

    .my-works-intro {
        display: none;
    }

    .profile-text  {
        display: none;
    }

    .contact-intro  {
        display: none;
    }

    .contact hr {
        display: none;

    }

    .get-in-touch   {
        display: none;
    }

    .contact h3 {
        display: none;
    }

    html, body {
        overflow-x: hidden;
        margin: 0;
        padding: 0;
        width: 100%;
    }

    .footer-content {
        display: none;
    }

}

/* ENABLE ON TABLET AND WEB */

@media (min-width: 769px) {
    .footer {
        display: grid;
        width: 100%;
        height: 60px;
        grid-template-columns: 1fr 1fr;
        align-items: center;
    }

    .footer-text, .footer-content {
        font-family: 'Unbounded', sans-serif;
        font-size: 12pt;
        color: var(--primary-color-text);
    }

    .footer-text {
        margin-left: 26px;
        text-align: start;
        font-weight: 200;
    }

    .footer-content {
        margin-right: 26px;
        text-align: right;
        font-weight: 800;
        text-transform: uppercase;
    }

    .footer-content a {
        color: var(--primary-color-text);
        text-decoration: none;
        transition: color 0.3s ease;
    }

    .footer-content a:hover {
        color: var(--primary-color-link);
    }
}
